import { Component,Input } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-icon',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './icon.component.html',
  styleUrls: ['./icon.component.css']
})
export class IconComponent {
  @Input() name:string='';
  @Input() typeIcon:'material' | 'fontawesome' | 'bootstrap' = 'material'; 
  @Input() classList:string='';
}
<!-- Ícono de Google Material Icons -->
<span *ngIf="typeIcon === 'material'" class="material-icons" [class]="classList">
    {{ name }}
  </span>

  <!-- Ícono de Font Awesome -->
  <i *ngIf="typeIcon === 'fontawesome'" [class]="'fa fa-' + name + ' ' + classList"></i>

  <!-- Ícono de Bootstrap Icons -->
  <i *ngIf="typeIcon === 'bootstrap'" [class]="'bi bi-' + name + ' ' + classList"></i>
